        List<Coroutine> coroutines = new Routine(Example(), this).Coroutines;

        // Due to the fact that nothing was done with the routine, this would return true.
        Log(coroutines.IsEmpty());
    }

    private IEnumerator Example()
    {
        yield return null;
    }
}

